Ext.define('VKM.view.FriendsInCommonItem', {
    extend: 'Ext.dataview.component.DataItem',

    requires: [
        'Ext.Img'
    ],

    config: {
        padding: 10,
        layout: 'hbox',
        itemCls: 'x-list-item',
        items: [
            {
                xtype: 'image',
                cls: 'x-white',
                docked: 'left',
                height: 150,
                itemId: 'albumCover',
                style: {
                    'background-size': 'cover'
                },
                width: 150
            }
        ]
    },

    updateRecord: function(record) {
        this.down('#albumCover').setSrc(record.get('photo_100'));
        this.down('#albumCover').setHtml(record.get('first_name')+' '+record.get('last_name'));

    }

});